import ExpenseItem from "./ExpenseItem";
import Card from "../UI/Card";
import "./Expenses.css";
import ExpensesFilter from "./ExpensesFilter";
import React, { useState } from "react";

function Expenses(props) {
  //setting the state
  const [filteredYear, setFilteredYear] = useState("2020");
  const [expenseItems, setExpenseItems] = useState(props.items);
  const filterChangeHandler = (selectedYear) => {
    console.log("in setYearInState 0");
    setFilteredYear(selectedYear);
    console.log("year in setYearInState1:" + selectedYear);
    console.log("year in setYearInState2:" + filteredYear);
    setExpenseItems(expenseItems.filter(x => {
       console.log(x.date.getyear());
       x.year=selectedYear;
       }));
  };
  return (
    <div>
      <Card className="expenses">
        <ExpensesFilter
          selected={filteredYear}
          onChangeFilter={filterChangeHandler}
        />
        {expenseItems.map((expense) => (
          <ExpenseItem
            key = {expense.id}
            title={expense.title}
            amount={expense.amount}
            date={expense.date}
          />
        ))}
        {console.log('Data From Expenses:'+JSON.stringify(props.items,null,4))}

      </Card>
    </div>
  );
}

export default Expenses;
